import React, { useState, useEffect } from "react";
import { Input } from "semantic-ui-react";
import ReactTable from "react-table-6";
import "react-table-6/react-table.css";

import "./App.css";

const originalData = [
  { firstName: "aaaaa", status: "Pending", visits: 155 },
  { firstName: "aabFaa", status: "Pending", visits: 155 },
  { firstName: "adaAAaaa", status: "Approved", visits: 1785 },
  { firstName: "aAaaaa", status: "Approved", visits: 175 },
  { firstName: "adaSaaa", status: "Cancelled", visits: 165 },
  { firstName: "aasaaa", status: "Cancelled", visits: 157 },
  { firstName: "aweaaaaaewea", status: "Approved", visits: 153 },
  { firstName: "aaaaaa", status: "Submitted", visits: 155 },
  { firstName: "aaaeweaa", status: "Pending", visits: 1555 },
  { firstName: "aabFaa", status: "Submitted", visits: 155 },
  { firstName: "adaAAadsdweaa", status: "Approved", visits: 17585 },
  { firstName: "aAaaaa", status: "Approved", visits: 175 },
];

const App = () => {
  const [data, setData] = useState(originalData);
  const [columns, setColumns] = useState([]);
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    let columns = [
      {
        Header: "First Name",
        accessor: "firstName",
        sortable: true,
        show: true,
        displayValue: " First Name",
      },
      {
        Header: "Status",
        accessor: "status",
        sortable: true,
        show: true,
        displayValue: "Status ",
      },
      {
        Header: "Visits",
        accessor: "visits",
        sortable: false,
        show: true,
        displayValue: " Visits ",
      },
    ];
    setColumns(columns);
  }, []);

  const handleChange = (event) => {
    setSearchInput(event.target.value);
  };

  useEffect(() => {
    globalSearch();
  }, [searchInput]);

  //   const globalSearch = () => {
  //     let filteredData = originalData.filter((value) => {
  //       return (
  //         value.firstName.toLowerCase().includes(searchInput.toLowerCase()) ||
  //         value.status.toLowerCase().includes(searchInput.toLowerCase()) ||
  //         value.visits
  //           .toString()
  //           .toLowerCase()
  //           .includes(searchInput.toLowerCase())
  //       );
  //     });
  //     setData(filteredData);
  //   };
  const globalSearch = () => {
    let filteredData = originalData.filter((value) => {
      return value.status.toLowerCase().includes(searchInput.toLowerCase());
    });
    setData(filteredData);
  };

  return (
    // <div>
    //   <br />
    //   <Input
    //     size="large"
    //     name="searchInput"
    //     value={searchInput || ""}
    //     onChange={handleChange}
    //     label="Search"
    //   />
    <div class="container">
      <br />
      <input
        class="search-input"
        size="large"
        name="searchInput"
        value={searchInput || ""}
        onChange={handleChange}
        placeholder="Search"
      />

      <br />
      <br />
      <ReactTable
        data={data}
        columns={columns}
        defaultPageSize={10}
        className="react-table"
      />
      {/* <table className="react-table">
        <ReactTable>
          <thead>columns={columns}</thead>
          <tbody>data={data}</tbody>
          defaultPageSize={10}
        </ReactTable>
      </table> */}
    </div>
  );
};

export default App;
