import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import ReactTable from "react-table-6";
import "react-table-6/react-table.css";
import debounce from "lodash/debounce";

import { fetchAsyncPsets } from "../features/recordSlice";
import { getAllRecords } from "../features/recordSlice";

const App = () => {
  const dispatch = useDispatch();
  const alldata = useSelector(getAllRecords);

  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    dispatch(fetchAsyncPsets());
  }, [dispatch]);

  useEffect(() => {
    if (alldata.api && Array.isArray(alldata.api)) {
      const extractedData = alldata.api.map(({ name, doi }) => ({
        name,
        doi,
      }));
      setData(extractedData);
    }
  }, [alldata]);

  useEffect(() => {
    let columns = [
      {
        Header: " Name",
        accessor: "name",
        sortable: true,
        show: true,
        displayValue: " First Name",
      },
      {
        Header: "DOI",
        accessor: "doi",
        sortable: true,
        show: true,
        displayValue: "Status ",
      },
    ];
    setColumns(columns);
  }, []);

  const handleChange = (event) => {
    setSearchInput(event.target.value);
  };

  const delayedSearch = debounce(() => {
    globalSearch();
  }, 300);

  useEffect(() => {
    delayedSearch();
    return delayedSearch.cancel; // Cleanup debounce on component unmount
  }, [searchInput, delayedSearch]);

  const globalSearch = () => {
    let filteredData = [];

    if (alldata.api && Array.isArray(alldata.api)) {
      filteredData = alldata.api.filter((value) => {
        if (!value || !value.name) {
          console.log("Skipping undefined value:", value);
          return false;
        }
        return value.name.toLowerCase().includes(searchInput.toLowerCase());
      });
    }

    console.log(filteredData);
    setData(filteredData);
  };

  return (
    <div className="container">
      <br />
      <input
        className="search-input"
        size="large"
        name="searchInput"
        value={searchInput || ""}
        onChange={handleChange}
        placeholder="Search"
      />

      <br />
      <br />
      {alldata.loading ? (
        <p>Loading...</p>
      ) : (
        <ReactTable
          data={data}
          columns={columns}
          defaultPageSize={10}
          className="react-table"
        />
      )}
    </div>
  );
};

export default App;
