import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Input } from "semantic-ui-react";
import ReactTable from "react-table-6";
import "react-table-6/react-table.css";

import "./App.css";

// import {
//   fetchAsyncPsets,,
// } from "../features/recordSlice";

import { fetchAsyncPsets } from "../features/recordSlice";
import { getAllRecords } from "../features/recordSlice";
import { all } from "axios";

// export const Data = () => {

// };

const originalData = [
  { firstName: "aaaaa", status: "Pending", visits: 155 },
  { firstName: "aabFaa", status: "Pending", visits: 155 },
  { firstName: "adaAAaaa", status: "Approved", visits: 1785 },
  { firstName: "aAaaaa", status: "Approved", visits: 175 },
  { firstName: "adaSaaa", status: "Cancelled", visits: 165 },
  { firstName: "aasaaa", status: "Cancelled", visits: 157 },
  { firstName: "aweaaaaaewea", status: "Approved", visits: 153 },
  { firstName: "aaaaaa", status: "Submitted", visits: 155 },
  { firstName: "aaaeweaa", status: "Pending", visits: 1555 },
  { firstName: "aabFaa", status: "Submitted", visits: 155 },
  { firstName: "adaAAadsdweaa", status: "Approved", visits: 17585 },
  { firstName: "aAaaaa", status: "Approved", visits: 175 },
];

const App = () => {
  const dispatch = useDispatch();
  // useEffect(() => {
  //   dispatch(fetchAsyncPsets());
  // }, [dispatch]);

  const alldata = useSelector(getAllRecords);

  const [data, setData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  useEffect(() => {
    dispatch(fetchAsyncPsets());
  }, [dispatch]);

  useEffect(() => {
    if (alldata && alldata.api && Array.isArray(alldata.api)) {
      const extractedData = alldata.api.map(({ name, doi }) => ({
        name,
        doi,
      }));
      setData(extractedData);
    }
  }, [alldata]);

  // const extractedData = alldata.api.map(({ name, doi }) => ({
  //   name,
  //   doi,
  // }));
  // console.log("Filter", typeof extractedData);
  // const [data, setData] = useState(extractedData);
  // const [columns, setColumns] = useState([]);
  // const [searchInput, setSearchInput] = useState("");

  useEffect(() => {
    let columns = [
      {
        Header: " Name",
        accessor: "name",
        sortable: true,
        show: true,
        displayValue: " First Name",
      },
      {
        Header: "DOI",
        accessor: "doi",
        sortable: true,
        show: true,
        displayValue: "Status ",
      },
      {
        Header: "Visits",
        accessor: "visits",
        sortable: false,
        show: true,
        displayValue: " Visits ",
      },
    ];
    setColumns(columns);
  }, []);

  const handleChange = (event) => {
    setSearchInput(event.target.value);
  };

  useEffect(() => {
    globalSearch();
  }, [searchInput]);

  const globalSearch = () => {
    let filteredData = originalData.filter((value) => {
      return value.status.toLowerCase().includes(searchInput.toLowerCase());
    });
    setData(filteredData);
  };

  return (
    <div className="container">
      <br />
      <input
        class="search-input"
        size="large"
        name="searchInput"
        value={searchInput || ""}
        onChange={handleChange}
        placeholder="Search"
      />

      <br />
      <br />
      <ReactTable
        data={data}
        columns={columns}
        defaultPageSize={10}
        className="react-table"
      />
    </div>
  );
};

export default App;
