import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import movieApi from "../api/psetsApi";

export const fetchAsyncPsets = createAsyncThunk(
  "api/fetchAsyncPsets",
  async () => {
    const response = await movieApi.get(`api/psets`);
    console.log("ALL DATA", response.data);
    return response.data;
  }
);

// export const fetchAsyncShows = createAsyncThunk(
//   "movies/fetchAsyncShows",
//   async () => {
//     const seriesText = "Friends";
//     const response = await movieApi.get(
//       `?apiKey=${APIKey}&s=${seriesText}&type=series`
//     );
//     return response.data;
//   }
// );

const initialState = {
  api: {},
};

const apiSlice = createSlice({
  name: "api",
  initialState,
  reducers: {
    addapi: (state, { payload }) => {
      state.api = payload;
    },
  },
  extraReducers: {
    [fetchAsyncPsets.pending]: () => {
      console.log("Pending");
    },
    [fetchAsyncPsets.fulfilled]: (state, { payload }) => {
      console.log("Fetched Successfully!");
      return { ...state, api: payload };
    },
    [fetchAsyncPsets.rejected]: () => {
      console.log("Rejected!");
    },
  },
});

export const getAllRecords = (state) => state.api;
export default apiSlice.reducer;
